mapper.select:

SELECT ctFolder_name, ctFolder_num
FROM cartFolder
WHERE uid= #{uid}


SELECT ct_num, roomNumber, guests, ck_in, ck_out, night, totalFee, ctFolder_name, ctFolder_num
FROM cartTable
WHERE ctFolder_num= #{ctFolder_num}


SELECT ctFolder_num 
FROM cartfolder 
WHERE uid = #{uid} AND ctFolder_name = #{ctFolder_name}
ORDER BY ctFolder_num DESC limit 1;
