mapper.select:

SELECT wdate 
FROM inquiry_host
WHERE inquiryno = #{inquiryno}


SELECT inquiryno, uid, suid, title, wdate, roomNumber, content, grpno, depth, open, rp_st
FROM inquiry_host
WHERE roomNumber = #{roomNumber}
ORDER BY grpno DESC, depth;


SELECT suid
FROM hoteltable
WHERE hotelNumber = #{hotelNumber}
