mapper.select:

SELECT uid, upw, uemail, uname, upnb, ugrd, ugender, wdate
FROM user_buyer
WHERE uid = #{uid}

mapper.update:

UPDATE user_buyer 
SET upw = #{upw} , uname =#{uname} , uemail = #{uemail}, upnb = #{upnb}
WHERE uid = #{uid}

mapper.select:

SELECT COUNT(*)
FROM user_buyer
WHERE uid = #{one} AND upw = #{two}


