FROM user_buyer
WHERE uid = #{uid}


SELECT COUNT(*)
FROM user_buyer
WHERE uemail = #{uemail}


SELECT COUNT(*)
FROM user_buyer
WHERE uid=#{one} AND upw=#{two}


SELECT ugrd
FROM user_buyer
WHERE uid = #{bgrd_uid} AND upw=#{bgrd_upw}


SELECT uid, upw
FROM user_buyer
WHERE uname = #{uname} AND uemail = #{uemail}

mapper.update:

UPDATE user_buyer
SET ugrd='F1'
WHERE uid = #{uid} AND upw =${upw}

mapper.select:

